examples:
Connect-Entra -Scopes 'Directory.ReadWrite.All'
$params = @{
    Id = '00aa00aa-bb11-cc22-dd33-44ee44ee44ee'
    RefObjectId = 'aaaaaaaa-0000-1111-2222-bbbbbbbbbbbb'
}
Add-EntraBetaFeatureRolloutPolicyDirectoryObject @params

Connect-Entra -Scopes 'Policy.Read.All, Application.ReadWrite.All'
$params = @{
    Id = 'bbbbbbbb-1111-1111-1111-cccccccccccc'
    RefObjectId = 'ffffffff-5555-6666-7777-aaaaaaaaaaaa'
}
Add-EntraBetaServicePrincipalPolicy @params

Connect-Entra -Scopes 'Policy.Read.PermissionGrant'
$permissionGrantPolicy = Get-EntraBetaPermissionGrantPolicy | Where-Object {$_.Id -eq 'my-custom-consent-policy'}
Get-EntraBetaPermissionGrantConditionSet -PolicyId $permissionGrantPolicy.Id -ConditionSetType 'includes'

Connect-Entra -Scopes 'Policy.ReadWrite.PermissionGrant'
$permissionGrantPolicy = Get-EntraBetaPermissionGrantPolicy | Where-Object {$_.Id -eq 'my-custom-consent-policy'}
New-EntraBetaPermissionGrantConditionSet -PolicyId $permissionGrantPolicy.Id -ConditionSetType 'includes' -PermissionType 'delegated'

Connect-Entra -Scopes 'Policy.ReadWrite.TrustFramework'
$params = @{
    InputFilePath = 'C:\InputPolicy.xml'
    OutputFilePath = 'C:\CreatedPolicy.xml'
}
New-EntraBetaTrustFrameworkPolicy @params

Connect-Entra -Scopes 'Policy.ReadWrite.ConditionalAccess','Policy.Read.All'
$policy = Get-EntraBetaConditionalAccessPolicy | Where-Object {$_.DisplayName -eq 'MFA policy'}
Remove-EntraBetaConditionalAccessPolicy -PolicyId $policy.Id

Connect-Entra -Scopes 'Policy.ReadWrite.ConditionalAccess','Policy.Read.All'
$policy = Get-EntraBetaNamedLocationPolicy | Where-Object {"$_.DisplayName -eq 'IP named location policy'"}
Remove-EntraBetaNamedLocationPolicy -PolicyId $policy.Id

Connect-Entra -Scopes 'DelegatedPermissionGrant.ReadWrite.All'
$sharePointSP = Get-EntraBetaServicePrincipal | Where-Object { $_.DisplayName -eq 'Microsoft.SharePoint' }
$sharePointOAuth2AllSitesRead = Get-EntraBetaOAuth2PermissionGrant | Where-Object { $_.ResourceId -eq $sharePointSP.Id } | Where-Object { $_.Scope -eq 'AllSites.Read' }
Remove-EntraBetaOAuth2PermissionGrant -OAuth2PermissionGrantId $sharePointOAuth2AllSitesRead.Id

Connect-Entra -Scopes 'Policy.ReadWrite.PermissionGrant'
$permissionGrantPolicy = Get-EntraBetaPermissionGrantPolicy | Where-Object { $_.Id -eq 'my-custom-consent-policy' }
$conditionSet = Get-EntraBetaPermissionGrantConditionSet -PolicyId $permissionGrantPolicy.Id -ConditionSetType 'includes' | Where-Object { $_.PermissionType -eq 'delegated' }
Remove-EntraBetaPermissionGrantConditionSet -PolicyId $permissionGrantPolicy.Id -ConditionSetType 'includes' -Id $conditionSet.Id

Connect-Entra -Scopes 'Policy.Read.ApplicationConfiguration'
$policy = Get-EntraBetaPolicy | Where-Object {$_.DisplayName -eq 'Microsoft User Default Recommended Policy'}
Remove-EntraBetaPolicy -Id $policy.Id

Connect-Entra -Scopes Policy.Read.All, Application.ReadWrite.All
$params = @{
    Id = 'bbbbbbbb-1111-1111-1111-cccccccccccc'
    PolicyId = 'ffffffff-5555-6666-7777-aaaaaaaaaaaa'
}
Remove-EntraBetaServicePrincipalPolicy @params

Connect-Entra -Scopes 'Policy.ReadWrite.ConditionalAccess', 'Policy.Read.All'
$policy = Get-EntraBetaConditionalAccessPolicy | Where-Object { $_.DisplayName -eq 'MFA policy' }
Set-EntraBetaConditionalAccessPolicy -PolicyId $policy.Id -DisplayName 'MFA policy updated'

Connect-Entra -Scopes 'Policy.ReadWrite.ConditionalAccess', 'Policy.Read.All'
$policy = Get-EntraBetaConditionalAccessPolicy | Where-Object { $_.DisplayName -eq 'MFA policy' }
Set-EntraBetaConditionalAccessPolicy -PolicyId $policy.Id -State 'Enabled'

Connect-Entra -Scopes 'Policy.ReadWrite.PermissionGrant'
$permissionGrantPolicy = Get-EntraBetaPermissionGrantPolicy | Where-Object { $_.Id -eq 'my-custom-consent-policy' }
$conditionSet = Get-EntraBetaPermissionGrantConditionSet -PolicyId $permissionGrantPolicy.Id -ConditionSetType 'includes' | Where-Object { $_.PermissionType -eq 'delegated' }
Set-EntraBetaPermissionGrantConditionSet -PolicyId $permissionGrantPolicy -ConditionSetType 'includes' -Id $conditionSet.Id -PermissionClassification 'low'

Connect-Entra -Scopes 'Policy.ReadWrite.PermissionGrant'
$policy = Get-EntraBetaPermissionGrantPolicy | Where-Object { $_.DisplayName -eq 'Microsoft User Default Recommended Policy' }
Set-EntraBetaPermissionGrantPolicy -Id $policy.Id -Description 'Updated description'

Connect-Entra -Scopes 'Policy.ReadWrite.PermissionGrant'
$policy = Get-EntraBetaPermissionGrantPolicy | Where-Object { $_.DisplayName -eq 'Microsoft User Default Recommended Policy' }
Set-EntraBetaPermissionGrantPolicy -Id $policy.Id -DisplayName 'Updated DisplayName'

Connect-Entra -Scopes 'Policy.ReadWrite.ApplicationConfiguration'
$policy = Get-EntraBetaPolicy | Where-Object { $_.DisplayName -eq 'Microsoft User Default Recommended Policy' }
Set-EntraBetaPolicy -Id $policy.Id -DisplayName 'NewUpdated'

Connect-Entra -Scopes 'Policy.ReadWrite.ApplicationConfiguration'
$policy = Get-EntraBetaPolicy | Where-Object { $_.DisplayName -eq 'Microsoft User Default Recommended Policy' }
Set-EntraBetaPolicy -Id $policy.Id -IsOrganizationDefault $false
